  render() {
    return (
      <div>
        <form onSubmit={this.props.addTodo} style={{ display: "flex" }}>
          <input
            type="text"
            style={{ flex: "10", padding: "5px" }}
            placeholder="Add Todo..."
          />
          <input type="submit" style={{ flex: "1" }} value="Submit" />
        </form>
      </div>
    );
  }
}

export default AddTodo;
